	var s = document.createElement( 'script' );
	var src = "//cdn.clicktale.com/js/www07/ptc/959721af-e707-44b6-9b6a-d14f3ec0f756.js";
	s.setAttribute( 'src', src );
	document.head.appendChild( s );
}

